svg.script:

var btn;		//defined later
var readout;	//defined later
var i=0;
var fieldConfig=["ALL","1B","C","INF","MINF","OF","P","PINF","POF","UTIL"];
var positionsArr=[];
var countCycles=0;

positionsArr[0]=document.getElementById("pitcher");
positionsArr[1]=document.getElementById("catcher");
positionsArr[2]=document.getElementById("first_base");
positionsArr[3]=document.getElementById("second_base");
positionsArr[4]=document.getElementById("short_stop");
positionsArr[5]=document.getElementById("third_base");
positionsArr[6]=document.getElementById("right_field");
positionsArr[7]=document.getElementById("center_field");
positionsArr[8]=document.getElementById("left_field");

var fCon={
	field_ALL:[0,1,2,3,4,5,6,7,8],
	field_1B:[2],
	field_C:[1],
	field_INF:[3,4,5],
	field_MINF:[3,4],
	field_OF:[6,7,8],
	field_P:[0],
	field_PINF:[0,3,4,5],
	field_POF:[0,6,7,8],
	field_UTIL:[0,3,4,5,6,7,8]
};

function showCorrectFieldingPosition(choiceEL){
	var pL=positionsArr.length;
	if (choiceEL!==undefined){
		var fL=choiceEL.length;
		for (var i=0; i<pL;i++){
			positionsArr[i].setAttribute("style", "display:none;");
			for (var f=0; f<pL;f++){
				if (choiceEL[f]===i){
					//console.log("choiceEL[i]===i: ",choiceEL[i]==i);
					//console.log("choiceEL[i]: ",choiceEL[i]);
					//console.log("(i): ",i);
					positionsArr[i].setAttribute("style", "display:block;fill:orange;stroke:red;");
				}
			}
		}
		return;
	}
	else{
		for (var i=0; i<pL;i++){
			positionsArr[i].setAttribute("style", "display:none;");
		}
		return;
	}
}

function determineFieldConfig(param){
	switch (param){
		case "ALL":
			var choiceEL=fCon.field_ALL;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "1B":
			var choiceEL=fCon.field_1B;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "P":
			var choiceEL=fCon.field_P;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "C":
			var choiceEL=fCon.field_C;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "INF":
			var choiceEL=fCon.field_INF;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "MINF":
			var choiceEL=fCon.field_MINF;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "OF":
			var choiceEL=fCon.field_OF;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "PINF":
			var choiceEL=fCon.field_PINF;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "POF":
			var choiceEL=fCon.field_POF;
			showCorrectFieldingPosition(choiceEL);
			break;
		case "UTIL":
			var choiceEL=fCon.field_UTIL;
			showCorrectFieldingPosition(choiceEL);
			break;
		default:
			if (choiceEL===undefined){
				//console.log("param has been turned off");	
				showCorrectFieldingPosition();
			}
			break;
	}
	return;
}

function updateReadout(param){
	readout.textContent='configuration: "'+param+'"';
}

function cycleFieldConfigs(){
	if (countCycles<fieldConfig.length-1){
		countCycles++;
	}
	else{
		countCycles=0;
	}
	determineFieldConfig(fieldConfig[countCycles]);
	updateReadout(fieldConfig[countCycles]);
}

function init(){
	var btn=document.getElementById("cycle_positions_button");
	btn.setAttributeNS(null,"style","cursor:pointer;");
	btn.onclick=cycleFieldConfigs;
	readout=document.getElementById("current_position_configuration_readout");
	determineFieldConfig();
	updateReadout("NONE");
}

window.onload=init;
